INSERT INTO `categories` (`id`, `category_title`, `description`, `background_color`, `status`, `displayed_in`, `icon`, `img_uri`, `createdAt`, `updatedAt`) VALUES
	(1, 'Computers', 'Computers category encompasses books on various aspects of computer science, programming, software development, hardware, and technology. Explore the vast realm of computational theories, coding languages, electronics, artificial intelligence, and emerging trends shaping the digital landscape.', 17, NULL, 0, NULL, NULL, '2023-09-11 19:07:14', '2023-09-22 09:56:33'),
	(2, 'ActionScript', 'ActionScript is a versatile programming language primarily used for creating interactive web applications and video games. It empowers developers to build captivating multimedia experiences, animations, and user interfaces, allowing for seamless interactivity and engaging user interactions.', 20, NULL, 0, NULL, NULL, '2023-09-11 19:07:15', '2023-09-22 09:56:36'),
	(3, 'Psychology', 'Psychology explores the human mind and behavior, shedding light on how individuals think, feel, and interact. This category encompasses a range of topics such as cognitive processes, personality, emotions, mental health, and social influences, offering valuable insights into the complexities of human nature.', 1, NULL, 1, NULL, NULL, '2023-09-11 19:19:10', '2023-09-22 09:57:29'),
